package org.example.Ex1;/*1) Есть абстрактный класс Переводчик, который имеет характеристики имя, возраст, пол и умеет переводить с одногоязыка на другой. Реализовать данный класс и создать несколько классов различных переводчиков с одного языка на другой.Например с английского на русский, с русского на немецки и т.п.Продумайте какова архитектура наследования будет в данном приложении. */public abstract class Translator {    private String name;    private int age;    private char gender;    public Translator(String name, int age, char gender) {        this.name = name;        this.age = age;        this.gender = gender;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public int getAge() {        return age;    }    public void setAge(int age) {        this.age = age;    }    public char getGender() {        return gender;    }    public void setGender(char gender) {        this.gender = gender;    }    public void translates() {        System.out.println("Я умею переводить!");    }}