package org.example.Ex2;public class House {    private Walls wall;    private Doors door;    private Roof roof;    private Window window;    private Inhabitant inhabitant;    public House(Inhabitant inhabitant) {        this.wall = new Walls();        this.door = new Doors();        this.roof = new Roof();        this.window = new Window();        this.inhabitant = inhabitant;    }    public Inhabitant getInhabitant() {        return inhabitant;    }    public void setInhabitant(Inhabitant inhabitant) {        this.inhabitant = inhabitant;    }    public Walls getWall() {        return wall;    }    public void setWall(Walls wall) {        this.wall = wall;    }    public Doors getDoor() {        return door;    }    public void setDoor(Doors door) {        this.door = door;    }    public Roof getRoof() {        return roof;    }    public void setRoof(Roof roof) {        this.roof = roof;    }    public Window getWindow() {        return window;    }    public void setWindow(Window window) {        this.window = window;    }    public void Print() {        System.out.println("Здесь живёт " + new Inhabitant(getInhabitant().getName(),                getInhabitant().getApartmentNumber(), getInhabitant().getFloor()));    }}