package org.example.Ex2;public class Inhabitant {    private String name;    private int apartmentNumber;    private int floor;    public Inhabitant(String name, int apartmentNumber, int floor) {        this.name = name;        this.apartmentNumber = apartmentNumber;        this.floor = floor;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public int getApartmentNumber() {        return apartmentNumber;    }    public void setApartmentNumber(int apartmentNumber) {        this.apartmentNumber = apartmentNumber;    }    public int getFloor() {        return floor;    }    public void setFloor(int floor) {        this.floor = floor;    }    @Override    public String toString() {        return "Inhabitant{" +                "name='" + name + '\'' +                ", apartmentNumber=" + apartmentNumber +                ", floor=" + floor +                '}';    }}